app.use('/api/marketplace', marketplaceRoutes);
app.use('/api/vendors', vendor_systemRoutes);
app.use('/api/transfers', transfersRoutes);
app.use('/api/scanner', ai_scannerRoutes);
app.use('/api/collections', collectionsRoutes);
